function getEveryGridIDArray(row, col) {
    if (col > 100) col = 100
    if (row > 100) row = 100
    let arr = []
    let r = 0
    for (r = 0; r < row; r++) {
        for (c = 0; c < col; c++) {
            arr[r * col + c] = 1 + c + (100 * (r + 1))
        }
    }
    return arr
}

