            if (data.block == 0) t = '空'
            else if (data.block == 1) t = '水'
            else if (data.block == 2) t = '熔岩'
            else if (data.block == 3) t = '石头'
            else if (data.block == 4) t = '黑曜石'
            else if (data.block == 5) t = '熔岩烤鸡'
