function petal_apothecary_craft() {
    let slots = player.botania.petal_apothecary.slots
    let put = {}
    for (let i = 0; i < 4; i++) {
        if (slots[i]) put[slots[i]] = put[slots[i]] ? put[slots[i]] + 1 : 1
    }
    if (put.white == 4) return "pure_daisy"
    if (put.red == 1 && put.brown == 2 && put.lightgray == 1) return "endoflame"
    if (put.lime == 1 && put.purple == 2 && put.pink == 1) return "rosa_arcana"
    return undefined
}

function petal_apothecary_consume(flower) {
    if (flower == "pure_daisy") player.map.botania.flower.white[1] = player.map.botania.flower.white[1].sub(tmp.botania.petal_apothecary_craft_effective_mult[flower].sub(1).times(4))
    else if (flower == "endoflame") {
        player.map.botania.flower.red[1] = player.map.botania.flower.red[1].sub(tmp.botania.petal_apothecary_craft_effective_mult[flower].sub(1))
        player.map.botania.flower.brown[1] = player.map.botania.flower.brown[1].sub(tmp.botania.petal_apothecary_craft_effective_mult[flower].sub(1).times(2))
        player.map.botania.flower.lightgray[1] = player.map.botania.flower.lightgray[1].sub(tmp.botania.petal_apothecary_craft_effective_mult[flower].sub(1))
    }
    else if (flower == "rosa_arcana") {
        player.map.botania.flower.lime[1] = player.map.botania.flower.lime[1].sub(tmp.botania.petal_apothecary_craft_effective_mult[flower].sub(1))
        player.map.botania.flower.purple[1] = player.map.botania.flower.purple[1].sub(tmp.botania.petal_apothecary_craft_effective_mult[flower].sub(1).times(2))
        player.map.botania.flower.pink[1] = player.map.botania.flower.pink[1].sub(tmp.botania.petal_apothecary_craft_effective_mult[flower].sub(1))
    }
}

function checkTPOA() {
    let g = player.botania.grid
    let livingwoodArr = [102, 104, 201, 205, 401, 405, 502, 504]
    let glimmeringArr = [103, 301, 305]
    let emptyArr = [202, 203, 204, 302, 303, 304, 402, 403, 404]
    let liv = 0
    let gli = 0
    let emp = 0
    let cor = 0
    for (let i = 0; i < 21; i++) {
        if (i >= 0 && i < 8) {
            if (g[livingwoodArr[i]] == 'livingwood') liv += 1
        }
        else if (i >= 8 && i < 11) {
            if (g[glimmeringArr[i - 8]] == 'glimmering') gli += 1
        }
        else if (i == 11) {
            if (g[503] == 'core') cor += 1
        }
        else if (i >= 12) {
            if (g[emptyArr[i - 12]] == 'none') emp += 1
        }
    }
    let complete = (liv == 8 && gli == 3 && emp == 9 && cor == 1)
    if (complete) player.botania.portal.complete = true,
        player.botania.portal.portalText = '精灵传送门已激活！但是它无法送你前往亚尔夫海姆这个精灵的国度……'
    else player.botania.portal.portalText = "精灵传送门不完整或者有不该填充的位置被填充"
}

function elftrade(id) {
    let tr = tmp.botania.alfheimTrades
    if (id == 1) {
        if (player.manasteel.points.lt(2)) return
        let effm = tr[1].effectiveMult
        player.elementium.points = player.elementium.points.add(effm)
        player.manasteel.points = player.manasteel.points.sub(effm.times(2))
        return
    }
    else if (id == 2) {
        if (player.manasteel.points.lt(2)) return
        let effm = tr[2].effectiveMult
        player.botania.livingwood = player.botania.livingwood.sub(effm.times(2))
        player.botania.dreamwood = player.botania.dreamwood.add(effm)
        return
    }
}

